from agno.agent import Agent
from agno.models.google import Gemini
from typing import List, Optional, Literal
from pydantic import BaseModel, Field
from datetime import date



class PublicationInfo(BaseModel):
    part: Optional[int] = Field(None, description="भाग (Part)")
    year: Optional[int] = Field(None, description="साल (Publication Year BS)")
    month: Optional[str] = Field(None, description="महिना (Publication Month)")
    issue_number: Optional[int] = Field(None, description="अंक (Issue Number)")

class Judge(BaseModel):
    name: str
    title: str = Field("Hon'ble Justice", description="e.g., माननीय न्यायाधीश श्री")

class Party(BaseModel):
    name: str
    role: Literal["petitioner", "respondent"]
    details: Optional[str] = Field(None, description="Further details like address, relationships")

class Advocate(BaseModel):
    name: str
    advocate_type: Literal["Senior Advocate", "Advocate"]
    represents: Literal["petitioner", "respondent"]

class CaseSummaryPoint(BaseModel):
    paragraph_reference: Optional[str] = Field(None, description="Reference to a specific point/chapter in the judgment, e.g., प्रकरण नं. १० (Point/Chapter No. 10)")
    point_text_nepali: str
    point_text_english_translation: Optional[str] = None

class CitedPrecedent(BaseModel):
    citation: str = Field(description="Full citation of the precedent, e.g., ने.का.प.२०६३, अङ्क ४, नि.नं. ७६८८")
    description: Optional[str] = Field(None, description="Brief note on the precedent's relevance if available in the text")

class GeneralRelevantLaw(BaseModel):
    name: str = Field(description="Name of the law as listed in the 'सम्बद्ध कानून' (Relevant Laws) section, e.g., 'नेपालको संविधान'")

class DiscussedLegalProvision(BaseModel):
    law_name: str = Field(description="Name of the Act, Constitution, or Rules, e.g., 'नेपालको संविधान', 'मालपोत ऐन, २०३४'")
    provision_identifier: str = Field(description="Specific article, section, number, or rule, e.g., 'धारा १३३(२)', 'दफा ३१', 'अ.बं. २७ नं.'")
    description_of_use_or_content_nepali: Optional[str] = Field(None, description="Summary of how the provision was used or its content, as mentioned in the judgment.")
    description_of_use_or_content_english: Optional[str] = Field(None, description="English translation of the Nepali description.")

class LegalQuestion(BaseModel):
    id: int
    question_nepali: str
    question_english_translation: Optional[str] = None

class CourtAction(BaseModel):
    action_type: Literal["order", "decision", "writ_issuance", "prior_order_in_case"]
    description_nepali: str
    description_english_translation: Optional[str] = None
    target_entity: Optional[str] = Field(None, description="e.g., Land Revenue Office, specific parties")
    writ_type: Optional[Literal["Certiorari", "Mandamus", "Habeas Corpus", "Prohibition", "Quo Warranto"]] = None
    date_of_action_bs: Optional[str] = None
    date_of_action_ad: Optional[date] = None

class SupremeCourtJudgment(BaseModel):
    # Metadata
    publication_info: Optional[PublicationInfo] = None
    court_name: str = Field("Supreme Court, Joint Bench", description="सर्वोच्च अदालत, संयुक्त इजलास")
    decision_date_bs: str = Field(description="फैसला मिति (Decision Date BS)")
    decision_date_ad: Optional[date] = Field(None, description="Decision Date AD")
    order_date_bs: str = Field(description="आदेश मिति (Order Date BS, often same as decision date for final order)")
    order_date_ad: Optional[date] = Field(None, description="Order Date AD")
    case_number: str = Field(description="e.g., ०७४-WO-०३१८")
    subject_matter_nepali: str = Field(description="विषय (Subject Matter)")
    subject_matter_english: Optional[str] = Field(None, description="English translation of subject matter")


agent = Agent(
    model=Gemini(id="gemini-2.0-flash", search=True),
    show_tool_calls=True,
    markdown=True,
    instructions="you are the helpful ai assistant and your job is to be friendly to user if they ask about latest news then only use search",
    add_datetime_to_instructions=True,
    # response_model=SupremeCourtJudgment,
    # use_json_mode=True,
    debug_mode=True
)
